import React, { useState, useEffect } from 'react';
import api from '../services/api';
import Receipt from '../components/Receipt';

const CashierDashboard = () => {
  const [menu, setMenu] = useState([]);
  const [cart, setCart] = useState([]);
  const [tableNumber, setTableNumber] = useState('');
  const [lastOrder, setLastOrder] = useState(null);

  useEffect(() => {
    const fetchMenu = async () => {
      const { data } = await api.get('/menu');
      setMenu(data);
    };
    fetchMenu();
  }, []);

  const addToCart = (item) => {
    const exists = cart.find(c => c._id === item._id);
    if (exists) {
      setCart(cart.map(c => c._id === item._id ? { ...c, quantity: c.quantity + 1 } : c));
    } else {
      setCart([...cart, { ...item, quantity: 1 }]);
    }
  };

  const removeFromCart = (id) => {
    setCart(cart.filter(c => c._id !== id));
  };

  const calculateTotal = () => cart.reduce((acc, item) => acc + (item.price * item.quantity), 0);

  const placeOrder = async () => {
    if (cart.length === 0) return alert('Cart is empty');
    try {
      const orderData = {
        items: cart.map(c => ({ menuItem: c._id, quantity: c.quantity, price: c.price })),
        totalAmount: calculateTotal(),
        tableNumber: parseInt(tableNumber) || 0
      };
      const { data } = await api.post('/orders', orderData);
      setLastOrder(data);
      setCart([]);
      setTableNumber('');
      alert('Order placed successfully!');
    } catch (error) {
      alert('Failed to place order');
    }
  };

  const handlePrint = () => {
    const printContent = document.getElementById('printable-receipt');
    const WinPrint = window.open('', '', 'width=900,height=650');
    WinPrint.document.write(printContent.innerHTML);
    WinPrint.document.close();
    WinPrint.focus();
    WinPrint.print();
    WinPrint.close();
  };

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Menu Section */}
      <div className="flex-1 p-6 overflow-y-auto">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold text-gray-800">Menu</h1>
          {lastOrder && (
            <button onClick={handlePrint} className="bg-blue-600 text-white px-4 py-2 rounded">
              Print Last Receipt
            </button>
          )}
        </div>
        <div className="grid grid-cols-2 gap-4 lg:grid-cols-4">
          {menu.map(item => (
            <div key={item._id} onClick={() => addToCart(item)} className="p-4 bg-white rounded-lg shadow cursor-pointer hover:shadow-lg transition">
              <h3 className="font-bold">{item.name}</h3>
              <p className="text-gray-600">${item.price}</p>
            </div>
          ))}
        </div>
      </div>

      {/* Cart / POS Section */}
      <div className="w-96 bg-white shadow-xl flex flex-col">
        <div className="p-6 flex-1 overflow-y-auto">
          <h2 className="text-2xl font-bold mb-4 border-b pb-2">New Order</h2>
          <input 
            type="number" 
            placeholder="Table Number" 
            className="w-full p-2 mb-4 border rounded"
            value={tableNumber}
            onChange={(e) => setTableNumber(e.target.value)}
          />
          {cart.map(item => (
            <div key={item._id} className="flex justify-between items-center mb-2 p-2 bg-gray-50 rounded">
              <div>
                <div className="font-medium">{item.name}</div>
                <div className="text-sm text-gray-500">${item.price} x {item.quantity}</div>
              </div>
              <button onClick={() => removeFromCart(item._id)} className="text-red-500 text-sm">Remove</button>
            </div>
          ))}
        </div>
        <div className="p-6 border-t bg-gray-50">
          <div className="flex justify-between text-xl font-bold mb-4">
            <span>Total:</span>
            <span>${calculateTotal().toFixed(2)}</span>
          </div>
          <button onClick={placeOrder} className="w-full bg-green-600 text-white py-3 rounded-lg font-bold hover:bg-green-700">
            PLACE ORDER
          </button>
        </div>
      </div>

      {/* Hidden Receipt for Printing */}
      <div style={{ display: 'none' }}>
        <Receipt order={lastOrder} />
      </div>
    </div>
  );
};

export default CashierDashboard;
