import React, { useState, useEffect } from 'react';
import api from '../services/api';

const AdminDashboard = () => {
  const [stats, setStats] = useState({ totalRevenue: 0, count: 0 });
  const [menuItems, setMenuItems] = useState([]);
  const [newItem, setNewItem] = useState({ name: '', price: '', category: '' });

  useEffect(() => {
    const fetchData = async () => {
      const statsRes = await api.get('/orders/stats');
      setStats(statsRes.data);
      const menuRes = await api.get('/menu');
      setMenuItems(menuRes.data);
    };
    fetchData();
  }, []);

  const handleCreate = async (e) => {
    e.preventDefault();
    const { data } = await api.post('/menu', newItem);
    setMenuItems([...menuItems, data]);
    setNewItem({ name: '', price: '', category: '' });
  };

  const handleDelete = async (id) => {
    await api.delete(`/menu/${id}`);
    setMenuItems(menuItems.filter(item => item._id !== id));
  };

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <h1 className="text-3xl font-bold mb-8">Admin Dashboard</h1>
      
      {/* Stats Summary */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-12">
        <div className="p-6 bg-blue-500 text-white rounded-lg shadow-md">
          <h2 className="text-xl opacity-80">Total Revenue</h2>
          <p className="text-4xl font-bold mt-2">${stats.totalRevenue.toFixed(2)}</p>
        </div>
        <div className="p-6 bg-purple-500 text-white rounded-lg shadow-md">
          <h2 className="text-xl opacity-80">Orders Completed</h2>
          <p className="text-4xl font-bold mt-2">{stats.count}</p>
        </div>
      </div>

      {/* Menu Management */}
      <div className="bg-white p-6 rounded-lg shadow-md mb-8">
        <h2 className="text-2xl font-bold mb-6">Manage Menu</h2>
        <form onSubmit={handleCreate} className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-8">
          <input type="text" placeholder="Name" className="p-2 border rounded" value={newItem.name} onChange={e => setNewItem({...newItem, name: e.target.value})} required />
          <input type="number" placeholder="Price" className="p-2 border rounded" value={newItem.price} onChange={e => setNewItem({...newItem, price: e.target.value})} required />
          <input type="text" placeholder="Category" className="p-2 border rounded" value={newItem.category} onChange={e => setNewItem({...newItem, category: e.target.value})} required />
          <button type="submit" className="bg-blue-600 text-white rounded font-bold">Add Item</button>
        </form>

        <div className="overflow-x-auto">
          <table className="w-full text-left">
            <thead>
              <tr className="border-b">
                <th className="py-2">Name</th>
                <th className="py-2">Category</th>
                <th className="py-2">Price</th>
                <th className="py-2 text-right">Actions</th>
              </tr>
            </thead>
            <tbody>
              {menuItems.map(item => (
                <tr key={item._id} className="border-b hover:bg-gray-50">
                  <td className="py-3">{item.name}</td>
                  <td className="py-3">{item.category}</td>
                  <td className="py-3">${item.price}</td>
                  <td className="py-3 text-right">
                    <button onClick={() => handleDelete(item._id)} className="text-red-600 font-medium">Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
