import React, { useState, useEffect } from 'react';
import api from '../services/api';
import socketIOClient from 'socket.io-client';

const KitchenDashboard = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const fetchOrders = async () => {
      const { data } = await api.get('/orders');
      setOrders(data.filter(o => ['pending', 'preparing'].includes(o.status)));
    };
    fetchOrders();

    const socket = socketIOClient('http://localhost:5000');
    socket.on('newOrder', (order) => {
      setOrders(prev => [...prev, order]);
    });
    socket.on('orderUpdated', (updatedOrder) => {
      setOrders(prev => prev.map(o => o._id === updatedOrder._id ? updatedOrder : o).filter(o => ['pending', 'preparing'].includes(o.status)));
    });

    return () => socket.disconnect();
  }, []);

  const updateStatus = async (id, status) => {
    await api.patch(`/orders/${id}/status`, { status });
  };

  return (
    <div className="p-6">
      <h1 className="mb-6 text-3xl font-bold text-gray-800">Kitchen Dashboard</h1>
      <div className="grid grid-cols-1 gap-6 md:grid-cols-2 lg:grid-cols-3">
        {orders.map(order => (
          <div key={order._id} className="p-4 bg-white rounded-lg shadow-md border-t-4 border-orange-500">
            <div className="flex justify-between items-center mb-2">
              <span className="font-bold">#{order.orderNumber}</span>
              <span className="px-2 py-1 text-xs bg-orange-100 text-orange-800 rounded">{order.status}</span>
            </div>
            <ul className="mb-4">
              {order.items.map((item, idx) => (
                <li key={idx} className="flex justify-between">
                  <span>{item.menuItem?.name} x {item.quantity}</span>
                </li>
              ))}
            </ul>
            <div className="flex gap-2">
              {order.status === 'pending' && (
                <button onClick={() => updateStatus(order._id, 'preparing')} className="flex-1 bg-blue-500 text-white py-2 rounded">Start Preparing</button>
              )}
              {order.status === 'preparing' && (
                <button onClick={() => updateStatus(order._id, 'ready')} className="flex-1 bg-green-500 text-white py-2 rounded">Mark Ready</button>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default KitchenDashboard;
