import React, { useState, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const { login } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await login(email, password);
      const user = JSON.parse(localStorage.getItem('user'));
      if (user.role === 'admin') navigate('/admin');
      else if (user.role === 'cashier') navigate('/cashier');
      else if (user.role === 'kitchen') navigate('/kitchen');
    } catch (error) {
      alert('Login failed. Please check your credentials.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-slate-100 px-4 font-sans">
      <div className="w-full max-w-md bg-white rounded-3xl shadow-2xl p-12 border border-slate-100">

        <div className="text-center mb-12">
          <div className="bg-emerald-600 text-white w-20 h-20 rounded-3xl flex items-center justify-center text-4xl mx-auto mb-6 shadow-xl shadow-emerald-200">
            üçΩÔ∏è
          </div>
          <h1 className="text-4xl font-black text-slate-900 tracking-tight">
            Resto<span className="text-emerald-600">POS</span>
          </h1>
          <p className="text-slate-500 font-medium mt-3 text-lg">Sign in to your dashboard</p>
        </div>

        <form onSubmit={handleSubmit} className="space-y-8">
          <div className="space-y-3">
            <label className="block text-sm font-bold text-slate-700 ml-1 uppercase tracking-wider">
              Email Address
            </label>
            <input
              type="email"
              required
              placeholder="admin@restopos.com"
              className="w-full px-6 py-5 rounded-2xl border-2 border-slate-100 bg-slate-50 focus:bg-white focus:border-emerald-500 focus:ring-4 focus:ring-emerald-50 outline-none transition-all placeholder:text-slate-300 font-semibold text-slate-800"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>

          <div className="space-y-3">
            <div className="flex justify-between items-center ml-1">
              <label className="text-sm font-bold text-slate-700 uppercase tracking-wider">
                Password
              </label>
              <a href="#" className="text-xs font-bold text-emerald-600 hover:text-emerald-700 transition-colors">Forgot?</a>
            </div>
            <input
              type="password"
              required
              placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
              className="w-full px-6 py-5 rounded-2xl border-2 border-slate-100 bg-slate-50 focus:bg-white focus:border-emerald-500 focus:ring-4 focus:ring-emerald-50 outline-none transition-all placeholder:text-slate-300 font-semibold text-slate-800"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>

          <button
            type="submit"
            disabled={loading}
            className={`w-full py-5 rounded-2xl text-white font-black text-xl transition-all active:scale-[0.97] shadow-2xl shadow-emerald-200 ${
              loading 
                ? 'bg-slate-300 cursor-not-allowed' 
                : 'bg-emerald-600 hover:bg-emerald-700'
            }`}
          >
            {loading ? 'AUTHENTICATING...' : 'LOGIN TO POS'}
          </button>
        </form>

        <div className="mt-16 text-center border-t border-slate-100 pt-8">
          <p className="text-[10px] font-black text-slate-300 uppercase tracking-[0.3em]">
            &copy; 2024 RestoPOS System. All rights reserved.
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
